import { useEffect, useRef, useState } from 'react';
import axios from 'axios';

function ChatBox() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const bottomRef = useRef(null);

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  const handleSend = async () => {
    const trimmedInput = input.trim();
    if (!trimmedInput) return;

    // Add user's message
    setMessages(prev => [...prev, { sender: 'user', text: trimmedInput }]);
    setInput('');
    setIsTyping(true);

    try {
      // Send to backend
      const res = await axios.post('http://localhost:5000/api/chat', { message: trimmedInput }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        }
      });

      // Add bot's response
      setMessages(prev => [...prev, { sender: 'bot', text: res.data.reply }]);
    } catch (err) {
      setMessages(prev => [...prev, { sender: 'bot', text: '⚠️ Something went wrong.' }]);
    } finally {
      setIsTyping(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-4">
      <h2 className="text-2xl font-bold mb-4">Chat with our Assistant</h2>

      <div className="h-96 overflow-y-auto border rounded p-3 bg-gray-50 shadow-sm">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`my-2 flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div className={`px-4 py-2 rounded-xl max-w-xs ${msg.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>
              {msg.text}
            </div>
          </div>
        ))}

        {isTyping && (
          <div className="text-sm text-gray-500 italic">Bot is typing...</div>
        )}

        <div ref={bottomRef} />
      </div>

      <div className="flex mt-4">
        <input
          className="flex-1 border px-3 py-2 rounded-l outline-none"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        />
        <button
          onClick={handleSend}
          className="bg-blue-600 text-white px-5 py-2 rounded-r"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatBox;
