import { useState } from 'react';
import axios from '../api';
import { useNavigate } from 'react-router-dom';

export default function Login({ onLogin }) {
  const [form, setForm] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const res = await axios.post('/login', form);
      onLogin(res.data.user); // lift user to App
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-20 p-6 bg-white shadow rounded-xl">
      <h2 className="text-xl font-bold mb-4 text-center">Login</h2>
      <input
        className="w-full p-2 mb-3 border rounded"
        placeholder="Email"
        value={form.email}
        onChange={(e) => setForm({ ...form, email: e.target.value })}
      />
      <input
        className="w-full p-2 mb-3 border rounded"
        placeholder="Password"
        type="password"
        value={form.password}
        onChange={(e) => setForm({ ...form, password: e.target.value })}
      />
      <button onClick={handleLogin} className="w-full bg-blue-600 text-white py-2 rounded">
        Login
      </button>
      {error && <p className="text-center text-sm mt-3 text-red-500">{error}</p>}
    </div>
  );
}
