import { useState } from 'react';
import axios from '../api';

export default function Register() {
  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleRegister = async () => {
    try {
      const res = await axios.post('/register', form);
      setMessage(res.data.message); // show success message
    } catch (err) {
      setMessage(err.response?.data?.error || 'Registration failed');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-20 p-6 bg-white shadow rounded-xl">
      <h2 className="text-xl font-bold mb-4 text-center">Register</h2>
      <input
        className="w-full p-2 mb-3 border rounded"
        placeholder="Name"
        value={form.name}
        onChange={(e) => setForm({ ...form, name: e.target.value })}
      />
      <input
        className="w-full p-2 mb-3 border rounded"
        placeholder="Email"
        value={form.email}
        onChange={(e) => setForm({ ...form, email: e.target.value })}
      />
      <input
        className="w-full p-2 mb-3 border rounded"
        placeholder="Password"
        type="password"
        value={form.password}
        onChange={(e) => setForm({ ...form, password: e.target.value })}
      />
      <button onClick={handleRegister} className="w-full bg-green-600 text-white py-2 rounded">
        Register
      </button>
      {message && <p className="text-center text-sm mt-3 text-gray-700">{message}</p>}
    </div>
  );
}
