// ChatBox.jsx
import React, { useState } from 'react';
import axios from '../api';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [typing, setTyping] = useState(false);
  const [input, setInput] = useState('');

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'user', text: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setTyping(true);

    try {
      const res = await axios.post('/ask', { question: input });
      const botMessage = { sender: 'bot', text: res.data.answer };
      setMessages(prev => [...prev, botMessage]);
    } catch {
      setMessages(prev => [...prev, { sender: 'bot', text: 'Failed to get a response.' }]);
    } finally {
      setTyping(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="bg-white rounded-xl shadow p-4 h-[400px] overflow-y-auto space-y-2">
        {messages.map((msg, idx) => (
          <div key={idx} className={`text-${msg.sender === 'user' ? 'right' : 'left'}`}>
            <span className={`inline-block px-4 py-2 rounded-xl ${msg.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'}`}>
              {msg.text}
            </span>
          </div>
        ))}
        {typing && <p className="italic text-gray-500">Assistant is typing...</p>}
      </div>
      <div className="flex mt-4">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          className="flex-grow border rounded-l-xl p-3"
          placeholder="Ask something..."
        />
        <button onClick={handleSend} className="bg-blue-600 text-white px-5 rounded-r-xl">
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBox;
