import { Link, useNavigate } from 'react-router-dom';

function Navbar() {
  const navigate = useNavigate();
  const isLoggedIn = !!localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className="bg-white shadow-md px-6 py-4 flex justify-between items-center">
      <Link to="/" className="text-xl font-bold text-blue-600 hover:text-blue-700 transition">
        ðŸ¤– CustomerQueryAssistant
      </Link>

      <div className="flex items-center space-x-4">
        {isLoggedIn && (
          <Link
            to="/chat"
            className="text-gray-700 hover:text-blue-600 transition font-medium"
          >
            Chat
          </Link>
        )}
        <Link
          to="/about"
          className="text-gray-700 hover:text-blue-600 transition font-medium"
        >
          About
        </Link>

        {!isLoggedIn ? (
          <>
            <Link
              to="/login"
              className="text-gray-700 hover:text-blue-600 transition font-medium"
            >
              Login
            </Link>
            <Link
              to="/register"
              className="text-gray-700 hover:text-blue-600 transition font-medium"
            >
              Register
            </Link>
          </>
        ) : (
          <button
            onClick={handleLogout}
            className="bg-blue-600 text-white px-4 py-1.5 rounded-md hover:bg-blue-700 transition font-medium"
          >
            Logout
          </button>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
